mapper.insert:
insert into blog_group (name, user_id, blog_id
  )
values (#{name,jdbcType=VARCHAR}, #{userId,jdbcType=INTEGER}, #{blogId,jdbcType=INTEGER}
  )

mapper.update:
update blog_group
set name = #{name,jdbcType=VARCHAR},
  user_id = #{userId,jdbcType=INTEGER},
  blog_id = #{blogId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, user_id, blog_id
from blog_group
where id = #{id,jdbcType=INTEGER}
